import React from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.css';
import {
  Row, Col, Card, CardBody, CardTitle, CardText, CardImg
} from 'reactstrap';
//import './index.css'


class ContentFeed extends React.Component {
  constructor() {
    super();

    this.state = {
      'items': []
    }
  }
  componentDidMount() {
    this.getItems();
  }

  getItems() {
    fetch('https://app-store-play.myshopify.com/admin/api/2020-01/products/count.json')
    .then(results => results.json())
    .then(results => this.setState({'items': results}));
  }
  render() {
    return (
      <ul>
        {this.state.items.map(function(vendor, index) {
          return <ContentItem item={vendor} key={index} />
        })}
      </ul>
    );
  }
}

ReactDOM.render(
  <ContentFeed />,
  document.getElementById('root')
);
